import React, { Component } from "react";

class Counter extends Component {
  state = {
    value: this.props.value,
    tags: [],
  };
  constructor() {
    console.log(this.props.value);
  }
  handleIncrement = (message) => {
    console.log(message);
    this.setState({ value: this.state.value + 1 });
    if (this.state.value + 1 > 0) {
      this.setState({ tags: this.state.tags.concat(this.state.value + 1) });
    }
  };
  handleDecrement = (message) => {
    console.log(message);
    this.setState({ value: this.state.value - 1 });
    let filteredArray = this.state.tags.filter(
      (tag) => tag !== this.state.value
    );
    this.setState({ tags: filteredArray });
  };
  render() {
    return (
      <React.Fragment>
        <button
          onClick={() => this.handleIncrement({ msg: "increment made" })}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        <span style={{ fontSize: 30 }} className={this.badgeClassGetter()}>
          {this.formatCount()}
        </span>
        <button
          onClick={() => this.handleDecrement({ msg: "decrement made" })}
          className="btn btn-secondary btn-sm"
        >
          Decrement
        </button>
        {this.renderTags()}
      </React.Fragment>
    );
  }
  renderTags() {
    if (this.state.tags.length === 0 && this.state.value === 0) {
      return <p>Please Add Tags!</p>;
    } else if (this.state.value < 0) {
      return <p>Stay Positive Man!</p>;
    }
    return (
      <div>
        <span style={{ fontSize: 25 }}>
          There are {this.state.tags.length} Tags!
        </span>
        <ul>
          {this.state.tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }
  badgeClassGetter() {
    let classes = "badge m-2 badge-";
    classes += this.state.value === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { value } = this.state;
    return value === 0 ? "Zero" : value;
  }
}

export default Counter;
