import React, { Component } from "react";
import Counter from "./counter";
class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 1 },
      { id: 2, value: 0 },
      { id: 3, value: 1 },
      { id: 4, value: 2 },
    ],
  };
  //controlled components do not have their own state
  handleDelete = (counterId) => {
    console.log("Hello", counterId);
    const filteredArray = this.state.counters.filter(
      (counter) => counter.id !== counterId
    );
    this.setState({ counters: filteredArray });
  };
  handleReset = () => {
    console.log(
      this.state.counters.map((c) => {
        return c.value;
      })
    );
    const counters = this.state.counters.map((c) => {
      c.value = 0;
      return c;
    });
    this.setState({ counters });
    console.log(
      this.state.counters.map((c) => {
        return c.value;
      })
    );
  };
  handleIncrement = (message, counterId) => {
    console.log(message, counterId);
  };

  render() {
    return (
      <React.Fragment>
        <button onClick={this.handleReset} className="btn-primary btn-sm m-2">
          Reset
        </button>
        {this.state.counters.map((counter) => (
          <Counter
            key={counter.id}
            onDelete={this.handleDelete}
            onIncrement={this.handleIncrement}
            counter={counter}
          />
        ))}
      </React.Fragment>
    );
  }
}

export default Counters;
