import React, { Component } from "react";

class Counter extends Component {
  render() {
    const { counter, onIncrement, onDecrement, onDelete } = this.props;
    const { id, value, tags } = counter;
    return (
      <div>
        <h2>Counter Number {id}</h2>
        <button
          onClick={() => onIncrement(counter)}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        <span style={{ fontSize: 30 }} className={this.badgeClassGetter()}>
          {this.formatCount()}
        </span>
        <button
          onClick={() => onDecrement(counter)}
          className="btn btn-secondary btn-sm"
        >
          Decrement
        </button>
        <button
          onClick={() => onDelete(id)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
        {this.renderTags()}
      </div>
    );
  }
  renderTags() {
    if (tags.length === 0 && value === 0) {
      return <p>Please Add Tags!</p>;
    } else if (value < 0) {
      return <p>Stay Positive Man!</p>;
    }
    return (
      <div>
        <span style={{ fontSize: 25 }}>There are {tags.length} Tags!</span>
        <ul>
          {tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }
  badgeClassGetter() {
    let classes = "badge m-2 badge-";
    classes += value === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { value } = counter;
    return value === 0 ? "Zero" : value;
  }
}
export default Counter;

//a component that owns the piece of the state should be the one modifying it
//this component is controlled by the counters component
