import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    tags: [],
  };
  constructor() {
    super();
    console.log(this.props);
    this.state.count = this.props.value;
  }
  handleIncrement = (message) => {
    console.log(message);
    this.setState({ count: this.state.count + 1 });
    if (this.state.count + 1 > 0) {
      this.setState({ tags: this.state.tags.concat(this.state.count + 1) });
    }
  };
  handleDecrement = (message) => {
    console.log(message);
    this.setState({ count: this.state.count - 1 });
    if (this.state.count <= 0) {
      return;
    }
    let filteredArray = this.state.tags.filter(
      (tag) => tag !== this.state.count
    );
    this.setState({ tags: filteredArray });
  };
  render() {
    return (
      <React.Fragment>
        <button
          onClick={() => this.handleIncrement({ msg: "increment made" })}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        <span style={{ fontSize: 30 }} className={this.badgeClassGetter()}>
          {this.formatCount()}
        </span>
        <button
          onClick={() => this.handleDecrement({ msg: "decrement made" })}
          className="btn btn-secondary btn-sm"
        >
          Decrement
        </button>
        {this.renderTags()}
      </React.Fragment>
    );
  }
  renderTags() {
    if (this.state.tags.length === 0 && this.state.count === 0) {
      return <p>Please Add Tags!</p>;
    } else if (this.state.count < 0) {
      return <p>Stay Positive Man!</p>;
    }
    return (
      <div>
        <span style={{ fontSize: 25 }}>
          There are {this.state.tags.length} Tags!
        </span>
        <ul>
          {this.state.tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }
  badgeClassGetter() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
