import React, { Component } from "react";

class Counter extends Component {
  state = {
    value: this.props.counter.value,
    tags: [],
  };
  /*
  handleIncrement = (message) => {
    console.log(message);
    this.setState({ value: this.state.value + 1 });
    if (this.state.value + 1 > 0) {
      this.setState({ tags: this.state.tags.concat(this.state.value + 1) });
    }
  };
  handleDecrement = (message) => {
    console.log(message);
    this.setState({ value: this.state.value - 1 });
    if (this.state.tags.length > 0) {
      let filteredArray = this.state.tags.filter(
        (tag) => tag !== this.state.value
      );
      this.setState({ tags: filteredArray });
    }
  };
  */
  render() {
    return (
      <div>
        <h2>Counter Number {this.props.counter.id}</h2>
        <button
          onClick={() =>
            this.props.onIncrement(
              { msg: "increment made" },
              this.props.counter
            )
          }
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        <span style={{ fontSize: 30 }} className={this.badgeClassGetter()}>
          {this.formatCount()}
        </span>
        <button
          onClick={() =>
            this.props.onDecrement(
              { msg: "decrement made" },
              this.props.counter.id
            )
          }
          className="btn btn-secondary btn-sm"
        >
          Decrement
        </button>
        <button
          onClick={() => this.props.onDelete(this.props.counter.id)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
        {this.renderTags()}
      </div>
    );
  }
  renderTags() {
    if (
      this.props.counter.tags.length === 0 &&
      this.props.counter.value === 0
    ) {
      return <p>Please Add Tags!</p>;
    } else if (this.props.counter.value < 0) {
      return <p>Stay Positive Man!</p>;
    }
    return (
      <div>
        <span style={{ fontSize: 25 }}>
          There are {this.props.counter.tags.length} Tags!
        </span>
        <ul>
          {this.props.counters.tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }
  badgeClassGetter() {
    let classes = "badge m-2 badge-";
    classes += this.props.counter.value === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { value } = this.props.counter;
    return value === 0 ? "Zero" : value;
  }
}

export default Counter;

//a component that owns the piece of the state should be the one modifying it
