import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"],
  };

  handleIncrement = () => {
    console.log("Increment Handler: ", this);
  };
  render() {
    React.createElement("div");
    return (
      <React.Fragment>
        <React.Fragment>
          <span style={{ fontSize: 30 }} className={this.badgeClassGetter()}>
            {this.formatCount()}
          </span>
          <button
            onClick={this.handleIncrement}
            className="btn btn-secondary btn-sm"
          >
            Increment
          </button>
          {this.renderTags()}
        </React.Fragment>
        <React.Fragment>
          <span style={{ fontSize: 30 }} className={this.badgeClassGetter()}>
            {this.formatCount()}
          </span>
          <button
            onClick={this.handleIncrement}
            className="btn btn-secondary btn-sm"
          >
            Increment
          </button>
          {this.renderTags()}
        </React.Fragment>
      </React.Fragment>
    );
  }
  renderTags() {
    if (this.state.tags.length === 0) {
      return <p>Please Add Tags!</p>;
    }
    return (
      <div>
        <span style={{ fontSize: 25 }}>
          There are {this.state.tags.length} Tags!
        </span>
        <ul>
          {this.state.tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }
  badgeClassGetter() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
