import React, { Component } from "react";

class Counter extends Component {
  render() {
    return (
      <div>
        <h2>Counter Number {this.props.counter.id}</h2>
        <button
          onClick={() => this.props.onIncrement(this.props.counter)}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        <span style={{ fontSize: 30 }} className={this.badgeClassGetter()}>
          {this.formatCount()}
        </span>
        <button
          onClick={() => this.props.onDecrement(this.props.counter)}
          className="btn btn-secondary btn-sm"
        >
          Decrement
        </button>
        <button
          onClick={() => this.props.onDelete(this.props.counter.id)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
        {this.renderTags()}
      </div>
    );
  }
  renderTags() {
    if (
      this.props.counter.tags.length === 0 &&
      this.props.counter.value === 0
    ) {
      return <p>Please Add Tags!</p>;
    } else if (this.props.counter.value < 0) {
      return <p>Stay Positive Man!</p>;
    }
    return (
      <div>
        <span style={{ fontSize: 25 }}>
          There are {this.props.counter.tags.length} Tags!
        </span>
        <ul>
          {this.props.counter.tags.map((tag) => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }
  badgeClassGetter() {
    let classes = "badge m-2 badge-";
    classes += this.props.counter.value === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    const { value } = this.props.counter;
    return value === 0 ? "Zero" : value;
  }
}

export default Counter;

//a component that owns the piece of the state should be the one modifying it
